import Layout from '../src/components/Layout';
import { Cookies } from 'react-cookie';
import fetch from 'isomorphic-unfetch';

//components

import Nav from '../src/components/dashboard/Nav';
import Footer from '../src/components/dashboard/Footer';
import Table from '../src/components/dashboard/Table-object'
import axios from 'axios';
import Router from 'next/router'
//cookies
const cookies = new Cookies();

const profesores = props => {
    let activo = cookies.get('activo')
    if (!activo) {
        return (
            <>
                <h1>Usuario Inactivo, vaya al login.</h1>
                <button type='button' onClick={() => {
                    Router.push('/')
                }}>
                    Login
                </button>
            </>
        );
    }
    return (
        <Layout>
            <div className='main'>
                <Nav />
                <br />
                <div className='container'>
                    <h1>
                        Profesores
                    </h1>
                    <ul>
                        {props.shows.map(show => (
                            <a>{show.user}</a>
                        ))}
                    </ul>

                </div>
                <Footer />
            </div>
            <style global jsx>{`
                html,
                body,
                body > div:first-child,
                div#__next,
                div#__next > div,
                div#__next > div > div {
                    height: 100%;
                }

                *{
                    box-sizing: border-box;
                }

                .main{
                    width: 100%;
                    height: 100%;
                    background: linear-gradient(180deg, #C2C0C0, white);
                } 

                h1{
                    margin-bottom: 30px;
                }
            `}</style>
        </Layout>
    );
}

profesores.getInitialProps = async function () {
    const response = await fetch(`http://167.172.142.165/users`, {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Authorization': 'Token 19c3e6789349d414ac66ae3c17a80c4e8a12debc'
        },
    })
    const data = await response.json();
    console.log(`Show data fetched. Count: ${data.length}`);

    return {
        shows: data.map(entry => entry.show)
    };
};


export default profesores;
