export default class extends React.Component {
    render() {
        return (
            <div className='contenedor'>
                <div className='login'>
                    <h1>Login</h1>
                </div>
                <style jsx>{`
                    .contenedor{
                        width: 100%; 
                        height: 100%;
                        background: linear-gradient(-90deg, #042DD6, #8658F7);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }

                    .login{
                        width: 40%;
                        height: 40%;
                        background: #DBE0F9;
                        text-align: center;
                        box-shadow: 0px 0px 1px 1px black;
                    }

                    h1{
                        font-size: 50px;
                        letter-spacing: 2px;
                        text-shadow: black 0.1em 0.1em 0.2em;
                `}</style>
            </div>
        );
    }
}
