import Layout from '../src/components/Layout';
import { Cookies } from 'react-cookie';

//components

import Nav from '../src/components/dashboard/Nav';
import Footer from '../src/components/dashboard/Footer';
import Table from '../src/components/dashboard/Table-object'
import axios from 'axios';
import Router from 'next/router'
//cookies
const cookies = new Cookies();

const profesores = () => {
    let activo = cookies.get('activo')
    if (!activo) {
        return (
            <>
                <h1>Usuario Inactivo, vaya al login.</h1>
                <button type='button' onClick={() => {
                    Router.push('/')
                }}>
                    Login
                </button>
            </>
        );
    }
    return (
        <Layout>
            <div className='main'>
                <Nav />
                <br />
                <div className='container'>
                    <h1>
                        Profesores
                    </h1>
                    <Table />
                </div>
                <Footer />
            </div>
            <style global jsx>{`
                html,
                body,
                body > div:first-child,
                div#__next,
                div#__next > div,
                div#__next > div > div {
                    height: 100%;
                }

                *{
                    box-sizing: border-box;
                }

                .main{
                    width: 100%;
                    height: 100%;
                    background: linear-gradient(180deg, #C2C0C0, white);
                } 

                h1{
                    margin-bottom: 30px;
                }
            `}</style>
        </Layout>
    );
}

profesores.getInitialProps = async function () {
    axios.get('http://167.172.142.165/users', {
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Token ' + token
        },
    })
        .then((response) => {
            console.log('response', response.data)

        })
        .catch((error) => {
            alert('error', error.response)
            dispatch(userUpdateProfileFail())

        })
};


export default profesores;
