export default class extends React.Component {

    render() {
        return (
            <>
                <div className="card">
                <img src="https://www.w3schools.com/howto/img_avatar.png"/>
                    <div className="container">
                        <h4><b>John Doe</b></h4>
                    </div>
                </div>
                <style jsx>{`

            img{

                height: 80%;
            }            

            .card {
                box-shadow: 0 4px 8px 0 rgba(0,0,0,0.2);
                transition: 0.3s;
                width: 95%;
                height: 95%;
              }

              .card:hover {
                box-shadow: 0 8px 16px 0 rgba(0,0,0,0.2);
              }

              .container {
                padding: 2px 16px;
              }
        `}</style>
            </>
        );
    }
}
