export default class extends React.Component {
    render() {
        return (
            <div className='contenedor'>
                <div className='login'>
                    <div className='formulario column'>
                        <form className="form-signin">
                            <h1 className='titulo'>Login</h1>
                            <div className='row part'>
                                <label for="inputEmail" >Email address</label>
                                <input type="email" className='espacio' id="inputEmail" placeholder="Email address" required="" autofocus=""></input>
                            </div>
                            <div className='row part'>
                                <label >Password</label>
                                <input type="password" id="inputPassword" placeholder="Password" required=""></input>
                            </div>
                            <div className='column bottom'>
                                <button type="submit">Sign in</button>
                                <p className="mt-5 mb-3 text-muted">© 2020-2021</p>
                            </div>

                        </form>
                    </div>
                </div>
                <style jsx>{`
                    .contenedor{
                        width: 100%; 
                        height: 100%;
                        background: linear-gradient(-90deg, #042DD6, #8658F7);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }

                    .login{
                        width: 40%;
                        height: 40%;
                        background: #DBE0F9;
                        box-shadow: 0px 0px 5px 1px black;
                    }

                    .login .formulario{
                        border: solid 2px;
                        width: 100%;
                        height: 100%;
                    }

                    .login .formulario .titulo{
                        font-size: 3rem;
                        letter-spacing: 2px;
                        text-align: center;
                    }

                    .login .formulario .part{
                        width: 50%;
                        margin: auto;
                        text-align:right;
                    }

                    .login .formulario .bottom{
                        width: 50%;
                        margin: auto;
                        text-align: center;
                    }
                `}</style>
            </div>
        );
    }
}
