export default class extends React.Component {
    render() {
        return (
            <>
                <div className="searchbar">
                    <input className="search_input" type="text" name="" placeholder="Search..." />
                    <button className="search_icon">
                        <i className="fas fa-search"></i>
                    </button>
                </div>
                <style jsx>{`
            .searchbar{
                margin-bottom: auto;
                margin-top: auto;
                height: 60px;
                background-color: #353b48;
                border-radius: 30px;
                padding: 10px;
            }
            .search_input{
                color: white;
                border: 0;
                outline: 0;
                background: none;
                width: 0;
                caret-color:transparent;
                line-height: 40px;
                transition: width 0.4s linear;
            }
            .searchbar:hover > .search_input{
                padding: 0 10px;
                width: 450px;
                caret-color:red;
                transition: width 0.4s linear;
            }
            .search_icon{
                background: transparent;
                color: #e74c3c;
                border: none;
            }
            .search_icon{
                height: 40px;
                width: 40px;
                float: right;
                display: flex;
                justify-content: center;
                align-items: center;
                border-radius: 50%;
                color:white;
                text-decoration:none;
                }
        `}</style>
            </>
        );
    }
}
