import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import fetch from 'isomorphic-unfetch';

function getModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        width: 800,
        height: 200,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

export default function Buscador() {

    const classes = useStyles();
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);
    const [id, setId, getId] = React.useState('1');


    const handleInputChange = (event) => {
        setId({
            id: event.target.value
        })
    }

    const handleSubmit = (evt) => {
        evt.preventDefault();
        alert(`Submitting Name ${name}`)
        handleClose(id);
    }

    const handleOpen = (id) => {
        alert(id)
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <>
            <div className="d-flex h-100">
                <div className="searchbar">
                    <form onSubmit={handleSubmit}>
                        <input className="search_input" type="text" name="id" onChange={handleInputChange} placeholder="Search..." required />
                        <button type='submit' className="search_icon" onClick={handleOpen}>
                            <i className="fas fa-search"></i>
                        </button>
                        <Modal
                            open={open}
                            onClose={handleClose}
                            aria-labelledby="simple-modal-title"
                            aria-describedby="simple-modal-description"
                        >
                            <div style={modalStyle} className={classes.paper}>

                            </div>
                        </Modal>
                    </form>
                </div>
            </div>
            <style jsx>{`
        .searchbar{
            margin-bottom: auto;
            margin-top: auto;
            height: 60px;
            background-color: #353b48;
            border-radius: 30px;
            padding: 10px;
        }
        .searchbar form .search_input{
            color: white;
            border: 0;
            outline: 0;
            background: none;
            width: 0;
            caret-color:transparent;
            line-height: 40px;
            transition: width 0.4s linear;
        }
        .searchbar:hover > .searchbar form .search_input{
            padding: 0 10px;
            width: 450px;
            caret-color:red;
            transition: width 0.4s linear;
        }
        .searchbar form .search_icon{
            background: transparent;
            color: #e74c3c;
            border: none;
        }
        .searchbar form .search_icon{
            height: 40px;
            width: 40px;
            float: right;
            display: flex;
            justify-content: center;
            align-items: center;
            border-radius: 50%;
            color:white;
            text-decoration:none;
            }
    `}</style>
        </>
    );
}
