import Link from 'next/link';

//components
import Settings from './Settings'

export default class extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            activar: false
        }
    }

    render() {
        return (
            <header className='header'>
                <div className='logo'>
                    <div>
                        <button type='button' className='button-hidden'>
                            <i class="fa fa-bars"></i>
                        </button>
                    </div>
                    <div>
                        <img src='https://coreui.io/react/demo/static/media/logo.406a451a.svg'></img>
                    </div>
                    <div>
                        <button type='button'>
                            <i class="fa fa-bars"></i>
                        </button>
                        <div className='image'>
                            <Settings />
                        </div>
                    </div>
                </div>
                <div className='menu'>
                    <div className='right'>
                        <button type='button' className='button-hidden' onClick={() => {
                            this.setState({ activar: !this.state.activar })
                        }}>
                            <i class="fa fa-bars"></i>
                        </button>
                    </div>
                    <ul className={this.state.activar ? "active" : ""}>
                        <li>
                            <Link href='#'>
                                <a>Dashboard</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Profesores</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Estudiantes</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Cursos</a>
                            </Link>
                        </li>
                    </ul>
                </div>
                <div className='espace'></div>
                <div className='profile btn-group'>
                    <div className='espace'></div>
                    <Settings />
                </div>
                <style jsx>{`
            .header{
                background: white;
                display: flex;
                flex-direction: row;
                width: 100%;
                height: 6%;
                border: solid 1px gray;
            }

            .header .logo{
                width: 10%;
                display: flex;
                justify-content: center;
                align-items: center;
                text-align: right;
            }

            .header .logo img{
                width: 60%;
                height: 30%;
                margin-right: 20px;
            }

            .header .logo button{
                color: gray;
                font-size: 1.3rem;
                background: transparent;
                border: none;
            }
            .header .logo .image{
                display : none;
            }

            .header .logo .button-hidden{
                display : none;
            }

            .header .menu{
                width: 30%;
                display: flex;                
                align-items: center;
                color: #73818f;
            }

            .header .menu button{
                display: none;
            }

            .header .menu ul{
                width: 80%;
                margin: 0;
                padding: 0;
            }

            .header .menu ul li{

                list-style: none;
                display: inline-block;
                padding: 20px;  
            }


            .header .menu ul li a{
                color: #73818f;
                text-decoration: none;
            }

            .header .menu ul li a:hover{            
                color: #000;  
            }

            .header .espace{
                width: 40%;
            }
            .header .profile{
                width: 20%;
                display: flex;
                justify-content: center;
                align-items: center;
                text-align: right;
            }

            .header .profile .espace{
                width: 75%;
            }            

            .header .profile #menu button {
                width: 2.5rem;
                height: 2.5rem;
                border-radius:150px;
                border: 1px solid;
            }

            .header .profile button:hover{
                opacity: 0.50;
                -moz-opacity: .50;
                filter:alpha (opacity=50);
              }

            .header .profile  #menu ul{
                list-style:none;
                margin:0;
                padding:0;
            }

            .header .profile #menu ul button{
                display:block;
                text-decoration:none;
                font-weight:400;
                padding:10px;
                text-transform:uppercase;
            }

            .header .profile #menu ul li {
                position:relative;
                float:left;
                margin:0;
                padding:0;
            }

            .header .profile #menu ul ul {
                display:none;
                position:absolute;
                top:100%;
                left:0;
                padding:0;
                border-radiux: 5px;
            }

            .header .profile #menu ul ul li {
                float:none;
                width:250px;
                margin-left: -200px;
                background: white;
                border: 1px solid;
            }

            .header .profile #menu ul ul button {
                line-height:120%;
                padding:10px 15px;
            }

            .header .profile #menu ul li:hover > ul {
                display:block;
            }

            @media (max-width: 1920px){
                .header .logo{
                    width: 15%;
                }

                .header .logo img{
                    width: 45%;
                }

                .header .menu{
                    width: 60%;
