                        margin: 10px auto;
                        box-sizing: border-box;
                        font-style: oblique
                        font-family: "Comic Sans MS", cursive, sans-serif;
                    }
                    .label{
                        font-size: 1.2rem;
                        font-weight: bold;
                    }
                    .espacio{
                        margin-left: 5px;
                        width: 260px;
                        height: 25px;
                        border-radius: 3px;
                    }
                    login .formulario .bottom{
                        width: 50%;
                        margin: auto;
                    }
                    .btm{
                        border-radius: 4px;
                        width: 120px;
                        height: 35px;
                        background: #042DD6;
                        color: #fff;
                        font-size: 1.2rem;
                    }                    
                    img{
                        width: 25%
                    }   
                    @media (max-width: 1920px){
                        .login{
                            width: 45%;
