import Link from 'next/link';

export default class extends React.Component {
    render() {
        return (
            <header className='header'>
                <div className='logo'>
                    <div>
                        <img src='https://coreui.io/react/demo/static/media/logo.406a451a.svg'></img>
                    </div>
                    <div>
                        <button type='button'>
                            <i class="fa fa-bars"></i>
                        </button>
                    </div>
                </div>
                <div className='menu'>
                    <ul>
                        <li>
                            <Link href='#'>
                                <a>Dashboard</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Profesores</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Estudiantes</a>
                            </Link>
                        </li>
                        <li>
                            <Link href='#'>
                                <a>Cursos</a>
                            </Link>
                        </li>
                    </ul>
                </div>
                <div className='espace'></div>
                <div className='profile'>
                    <div className='espace'></div>
                    <div className='image'>

                    </div>
                </div>
                <style jsx>{`
            .header{
                background: white;
                display: flex;
                flex-direction: row;
                width: 100%;
                height: 6%;
                border: solid 1px gray;
            }

            .header .logo{
                width: 10%;
                display: flex;
                justify-content: center;
                align-items: center;
                text-align: right;
                border: 1px solid;
            }

            .header .logo img{
                width: 60%;
                height: 30%;
                margin-right: 20px;
            }

            .header .logo button{
                color: gray;
                font-size: 1.3rem;
                background: transparent;
                border: none;
            }

            .header .menu{
                width: 30%;
                display: flex;                
                align-items: center;
                color: #73818f;
                border: 1px solid;
            }

            .header .menu ul{
                width: 80%;
                margin: 0;
                padding: 0;
            }

            .header .menu ul li{

                list-style: none;
                display: inline-block;
                padding: 20px;  
            }


            .header .menu ul li a{
                color: #73818f;
                text-decoration: none;
            }

            .header .menu ul li a:hover{            
                color: #000;  
            }

            .header .espace{
                width: 40%;
                border: 1px solid;
            }
            .header .profile{
                width: 20%;
                display: flex;
                justify-content: center;
                align-items: center;
                text-align: right;
                border: 1px solid;
            }

            .header .profile .espace{
                width: 75%;
            }

            .image {
                width: 2.5rem;
                height: 2.5rem;
                border-radius:150px;
                border: 1px solid;
            }

            @media (max-width: 400px){
                .header{
                    background: black;
                    flex-direction: row;
                    border: solid 1px gray;
                }

                .header .logo{
                    width: 100%;
                }
            }
        `}</style>
            </header>
        );
    }
}
