export default class extends React.Component {
    render() {
        return (
            <div className='contenedor'>
                <div className='login'>
                    <div className='formulario column'>
                        <form className="form-signin">
                            <h1 className='titulo'>Login</h1>
                            <img src='https://pngimage.net/wp-content/uploads/2018/06/login-png-10.png'></img>
                            <div className='row part'>
                                <label className='label'>Username: </label>
                                <input type="email" className='espacio' id="inputEmail" required ></input>
                            </div>
                            <div className='row part'>
                                <label className='label' >Password: </label>
                                <input type="password" className='espacio' id="inputPassword" required></input>
                            </div>
                            <div className='column bottom'>
                                <button className='btm' type="submit">Sign in</button>
                                <p className="label">Â© 2020-2021</p>
                            </div>

                        </form>
                    </div>
                </div>
                <style jsx>{`
                    .contenedor{
                        width: 100%; 
                        height: 100%;
                        background: linear-gradient(-90deg, #042DD6, #8658F7);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }

                    .login{
                        width: 40%;
                        height: 50%;
                        background: #DBE0F9;
                        box-shadow: 0px 0px 5px 1px black;
                    }


                    .login .formulario{
                        width: 100%;
                        height: 100%;
                        text-align: center;
                    }


                    .login .formulario .part{
                        width: 50%;
                        margin: 10px auto;
                        text-align:right;
                        box-sizing: border-box;
                        font-style: oblique
                        font-family: "Comic Sans MS", cursive, sans-serif;
                    }

                    .login .formulario .titulo{
                        font-size: 3rem;
                        letter-spacing: 2px;

                    }

                    .label{
                        font-size: 1.5rem;
                        font-weight: bold;
                    }

                    login .formulario .bottom{
                        width: 50%;
                        margin: auto;
                    }

                    img{
                        width: 20%;
                    }

                    @media (min-width: 1280px){


                        .espacio{
                            margin-left: 10px;
                            width: 240px;
                            height: 35px;
                            border-radius: 4px;
                        }

                        .label{
                            font-size: 1.2rem;
                            font-weight: bold;
                        }

                        .btm{
                            border-radius: 4px;
                            width: 120px;
                            height: 45px;
                            background: #042DD6;
                            color: #fff;
                            font-size: 1.4rem;
                        }

                        img{
                            width: 15%;
                        }
                    }

                    @media (min-width: 1400px){                      

                        .espacio{
                            margin-left: 10px;
                            width: 140px;
                            height: 35px;
                            border-radius: 4px;
                        }

                        .label{
                            font-size: 1.4rem;
                            font-weight: bold;
                        }

                        .btm{
                            border-radius: 4px;
                            width: 120px;
                            height: 45px;
                            background: #042DD6;
                            color: #fff;
                            font-size: 1.4rem;
                        }

                        img{
                            width: 19%;
                        }


                    }

                    @media (min-width: 1920px){

                        .espacio{
                            margin-left: 40px;
                            width: 240px;
                            height: 30px;
                            border-radius: 3px;
                        }

                        .btm{
                            border-radius: 4px;
                            width: 120px;
                            height: 35px;
                            background: #042DD6;
                            color: #fff;
                            font-size: 1.2rem;
                        }

                    }
                `}</style>
            </div>
        );
    }
}
