export default class extends React.Component {
    render() {
        return (
            <div className='contenedor'>
                <div className='login'>
                    <div className='formulario column'>
                        <form className="form-signin">
                            <h1 className='titulo'>Login</h1>
                            <img src='https://pngimage.net/wp-content/uploads/2018/06/login-png-10.png'></img>
                            <div className='row part'>
                                <label className='label'>Username: </label>
                                <input type="email" className='espacio' id="inputEmail" required ></input>
                            </div>
                            <div className='row part'>
                                <label className='label' >Password: </label>
                                <input type="password" className='espacio' id="inputPassword" required></input>
                            </div>
                            <div className='column bottom'>
                                <button className='btm' type="submit">Sign in</button>
                                <p className="label">© 2020-2021</p>
                            </div>

                        </form>
                    </div>
                </div>
                <style jsx>{`
                    .contenedor{
                        width: 100%; 
                        height: 100%;
                        background: linear-gradient(-90deg, #042DD6, #8658F7);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }

                    .login{
                        width: 40%;
                        height: 50%;
                        background: #DBE0F9;
                        box-shadow: 0px 0px 5px 1px black;
                    }

                    .login .formulario{
                        width: 100%;
                        height: 100%;
                        text-align: center;
                    }

                    .login .formulario .titulo{
                        font-size: 4rem;
                        letter-spacing: 2px;

                    }

                    .login .formulario .part{
                        width: 50%;
                        margin: 10px auto;
                        text-align:right;
                        box-sizing: border-box;
                        font-style: oblique
                        font-family: "Comic Sans MS", cursive, sans-serif;
                    }


                    .label{
                        font-size: 1.2rem;
                        font-weight: bold;
                    }
                    .espacio{
                        margin-left: 5px;
                        border-radius: 3px;
                    }

                    login .formulario .bottom{
                        width: 50%;
                        margin: auto;
                    }

                    .btm{
                        border-radius: 4px;
                        width: 120px;
                        height: 35px;
                        background: #042DD6;
                        color: #fff;
                        font-size: 1.2rem;
                    }

                    img{
                        width: 20%;
                    }

                    @media (min-width: 400px){  

                        .login{
                            width: 80%;
                            height: 40%;
                        }

                        .login .formulario .titulo{
                            font-size: 1.8rem;

                        }

                        .espacio{
                            width: 60px;
                            height: 25px;
                        }

                        .label{
                            font-size: 0.9rem;
                            font-weight: bold;
                        }

                        .btm{
                            width: 80px;
                            height: 35px;
                            font-size: 0.9rem;
                        }

                        img{
                            width: 45%;
                        }
                    }


                `}</style>
            </div>
        );
    }
}
