export default class extends React.Component {
    render() {
        return (
            <div className='contenedor'>
                <div className='login'>
                    <h1 className='titulo'>Login</h1>
                    <div>
                        <form className="form-signin formulario">
                            <div className=''>
                                <label for="inputEmail" className="sr-only">Email address</label>
                                <input type="email" id="inputEmail" className="form-control" placeholder="Email address" required="" autofocus=""></input>
                            </div>
                            <div className=''>
                                <label for="inputPassword" className="sr-only">Password</label>
                                <input type="password" id="inputPassword" className="form-control" placeholder="Password" required=""></input>
                            </div>
                            <button className="btn btn-lg btn-primary btn-block" type="submit">Sign in</button>
                            <p className="mt-5 mb-3 text-muted">© 2020-2021</p>
                        </form>
                    </div>
                </div>
                <style jsx>{`
                    .contenedor{
                        width: 100%; 
                        height: 100%;
                        background: linear-gradient(-90deg, #042DD6, #8658F7);
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }

                    .login{
                        width: 40%;
                        height: 40%;
                        background: #DBE0F9;
                        text-align: center;
                        box-shadow: 0px 0px 5px 1px black;   
                    }

                    .titulo{
                        font-size: 2rem;
                        letter-spacing: 2px;
                    }

                    .formulario{
                        border: solid 1px;
                    }
                `}</style>
            </div>
        );
    }
}
